<template>    <button class="g-button" :class="{[`icon-${iconPosition}`]:true}">        <!--如果有icon就给你传入图标，没有就不传-->        <g-icon v-if="icon" :name="icon"></g-icon>        <div class="content">            <slot></slot>        </div>    </button></template><script>    export default {        props:{            icon:{},            iconPosition:{                type:String,                default:'left'            },            validator: function (value) {                return !(value !== 'right' && value !== 'left');            }        }    }</script><style lang="scss">    .g-button {        font-size: var(--font-size);        height: var(--button-height);        padding: 0 1em;        border-radius: var(--border-radius);        background: var(--button-bg);        border: 1px solid var(--border-color);        display: inline-flex;        justify-content: center;        align-items: center;        vertical-align: middle;    }    .g-button:hover {        border-color: var(--border-color-hover);    }    .g-button:active {        background-color: var(--button-active-bg);    }    .g-button:focus {        outline: none;    }    .g-button.icon-right {        > .icon {            order: 2;            margin-right: 0px;            margin-left: .1em;        }        > .content {            order: 1;        }    }    .g-button.icon-left {        > .icon {            order: 1;            margin-left: 0;            margin-right: .1em;        }        > .content {            order: 2;        }    }</style>